mapper.select:

select b.no, b.title, b.content, u.name, b.hit, b.reg_date, b.group_no, b.order_no
from board b, user u
where b.group_no=#{groupno}
	and b.order_no=#{orderno}
	and b.user_no = u.no

mapper.update:

update board
	set title=#{title}, content=#{content}
where no=#{no}


update board
	set title="삭제된 글입니다", content="글쓴이에 의해 삭제됐습니다"
where no=#{no}


update board
	set hit=hit+1
where no=#{no}
