mapper.update:

update board
	set order_no=order_no+1
	where group_no in (SELECT * FROM (SELECT b.group_no from board b where b.no=#{parent_no}) a )
			and no !=#{no}
			and order_no >= #{order_no}

mapper.select:

select b.no, b.title, b.content, u.name, b.hit, b.reg_date, b.group_no, b.order_no
from board b, user u
where b.group_no=#{groupno}
	and b.order_no=#{orderno}
	and b.user_no = u.no

mapper.update:

update board
	set title=#{title}, content=#{content}, reg_date=now()
where no=#{no}
